mapper.select:
SELECT ps.f_id as shelfId, p.f_id as productId, p.f_name as productName, esm.f_name as merchantName from t_ebuy_product_shelf ps INNER JOIN t_ebuy_product_type pt on ps.t_ebuy_product_type_id = pt.f_id and pt.f_sys0_del_state = 0 and ps.f_up_shelf_state = 0 INNER JOIN t_ebuy_product p on ps.t_ebuy_product_id = p.f_id and p.f_sys0_del_state = 0 INNER JOIN t_ebuy_supply_merchant esm on p.t_supply_merchant_f_id = esm.f_id and esm.f_sys0_del_state = 0 WHERE ps.f_sys0_del_state = 0 and ps.f_up_shelf_state = 0 AND EXISTS (SELECT 1 FROM t_ebuy_supply_merchant_has_t_ebuy_delivery_method ESMHTEDM INNER JOIN t_ebuy_delivery_method EDM ON EDM.f_id = ESMHTEDM.t_ebuy_delivery_method_f_id AND EDM.f_sys0_del_state = 0 WHERE ESMHTEDM.t_ebuy_supply_merchant_f_id = esm.f_id AND ESMHTEDM.f_sys0_del_state = 0)
and pt.f_wlApp_type = #{appType}
and (p.f_id = #{qryStr} or p.f_name like concat('%', #{qryStr}, '%'))
and esm.f_id = #{supplyMerchantId}
limit #{begin}, #{length}

SELECT ea.f_id AS id, ea.f_code AS code, ea.f_type AS `type`, ea.f_tittle as tittle, ea.f_pic_name AS picName, date_format(ea.f_start_time,'%Y-%m-%d %T') AS startTime, date_format(ea.f_end_time,'%Y-%m-%d %T') AS endTime, ea.f_order AS `order`, CASE WHEN now() < ea.f_start_time THEN 1 WHEN ea.f_start_time <= now() AND now() < ea.f_end_time THEN 2 ELSE 3 END AS `status`, IF ( ou1.f_real_name IS NULL OR ou1.f_real_name = '', ou1.f_user_account, ou1.f_real_name ) AS addMan, IF ( ou2.f_real_name IS NULL OR ou2.f_real_name = '', ou2.f_user_account, ou2.f_real_name ) AS updateMan FROM t_ebuy_advertise ea left join t_oms_user ou1 on ou1.f_id=ea.f_sys0_add_user left join t_oms_user ou2 on ou2.f_id=ea.f_sys0_upd_user WHERE ea.f_sys0_del_state = 0 AND (ea.f_link_url like '%/themeProductAd.%?advId=%' or ea.f_link_url like '%themeAdv/productList.html?advId=%')
ORDER BY ea.f_order DESC, ea.f_start_time DESC
limit #{begin}, #{length}

select tac.f_id countryId, ap.f_id provinceId, ac.f_id cityId, ab.f_id blockId, gb.f_id gbId
FROM t_address_city ac
LEFT JOIN t_address_block ab ON ab.t_city_f_id = ac.f_id AND ab.f_sys0_del_state = 0
LEFT JOIN t_group_building gb on gb.t_block_f_id = ab.f_id and gb.f_sys0_del_state = 0
LEFT JOIN t_address_province ap ON ap.f_id = ac.t_province_f_id AND ap.f_sys0_del_state = 0
LEFT JOIN t_address_country tac ON tac.f_id = ap.t_address_country_f_id AND tac.f_sys0_del_state = 0
WHERE ac.f_sys0_del_state = 0

    and gb.f_id = #{gbId}


    and ab.f_id = #{blockId}


    and ac.f_id = #{cityId}


    and ap.f_id = #{provinceId}


    and tac.f_id = #{countryId}

limit 1

SELECT t.* FROM (
)as t ORDER BY t.order DESC, t.startTime DESC
LIMIT #{begin}, #{length}
