mapper.select:
SELECT _tmp.* FROM (
SELECT TPC.f_id PcId,TPC.f_name pcName,TGB.f_name parking,TCNL.t_car_num carNum, CASE WHEN TCNPL.f_status=1 THEN '' ELSE TRHCN.f_real_name END userName, CASE WHEN TCNPL.f_status=0 THEN TRHCN.f_cellphone ELSE '' END phoneNum, date_format(TCNPL.f_pay_time,'%Y-%m-%d %T') payTime, CASE WHEN TCNPL.f_pay_num >=12 THEN 3 WHEN TCNPL.f_pay_num = 0 THEN 1 ELSE 2 END AS carType,TCNPL.f_pay_num payNum, TCNPL.f_fee payMoney, IFNULL(TCNPL.f_coupon_amount, 0) couponAmount, TCNPL.f_status payType, CASE WHEN TCNPL.f_status = 0 THEN TEO.f_order_no ELSE '' END payFlown, TB.f_name AS buildingname,TRR.f_num_tail roomNum,TRR.f_unit_name AS unitName, TCNPL.f_need_bill AS needBill, TEO.f_pay_method AS payMethod, GB2.f_name AS gbName, IFNULL(EOE.f_wy_coupon_amount, 0) wyCouponAmount
GROUP BY TCNPL.f_status, TCNPL.t_car_num_id, TCNPL.f_pay_time UNION ALL
SELECT RSA.f_mini_role_id PcId, RSA.f_role_name pcName, fr.f_residential parking, fb.f_car_num carNum, fb.f_buy_person userName, fb.f_buy_mobile phoneNum, date_format(RSA.f_goal_rev_time,'%Y-%m-%d %T') payTime, 2 AS carType,1 AS payNum, RSA.f_amount*100 payMoney, 0 couponAmount, 2 payType, '' payFlown, B.f_name AS buildingname,RR.f_num_tail roomNum,RR.f_unit_name AS unitName, 0 AS needBill, '' AS payMethod, GB2.f_name AS gbName, 0 wyCouponAmount
) _tmp ORDER BY _tmp.payTime DESC LIMIT #{_begin},#{_length}
