package com.cnfantasia.server.api.lotteryDraw.entity;/* import java.io.Serializable;*/import com.cnfantasia.server.domain.pub.BaseEntity;import java.math.BigInteger;/** * 描述:(幸运抽奖奖品表) 实体类 *  * @version 1.00 * @author null *  */public class LotteryDrawProductEntity extends BaseEntity{	private static final long serialVersionUID = 1L;	/**  */	private BigInteger id;	/** 奖品名称 */	private String name;	/** 商品描述（价值2480元/台） */	private String desc;	/** 参与抽奖状态（2参与，1不参与） */	private Integer status;	public BigInteger getId() {		return id;	}	public void setId(BigInteger id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getDesc() {		return desc;	}	public void setDesc(String desc) {		this.desc = desc;	}	public Integer getStatus() {		return status;	}	public void setStatus(Integer status) {		this.status = status;	}}