mapper.select:
SELECT ,, EPFG.f_price_discount fightPrice,date_format(EPFG.f_start_date,'%Y-%m-%d %T') startDate,date_format(EPFG.f_expire_date,'%Y-%m-%d %T') expireDate,EPFG.f_fight_num fightNum,(select f_url_mini from t_ebuy_product_fight_pic pfp where pfp.t_ebuy_product_f_id = EPFG.t_ebuy_product_f_id order by f_id desc limit 1) picUrl,EPFG.f_id fightProductId, EPS.f_id productShelfId FROM t_ebuy_product_fight_groups EPFG INNER JOIN t_ebuy_product EP ON EPFG.t_ebuy_product_f_id = EP.f_id and EP.f_status = 0 INNER JOIN t_ebuy_product_shelf EPS ON EPS.t_ebuy_product_id = EP.f_id and EPS.f_up_shelf_state = 0 LEFT JOIN t_ebuy_fight_order EFO ON EPFG.f_id = EFO.t_ebuy_product_fight_groups_f_id LEFT JOIN t_ebuy_product_fight_pic EPP ON EPP.t_ebuy_product_f_id=EP.f_id
INNER JOIN t_ebuy_product_type EPT ON EPT.f_id = EPS.t_ebuy_product_type_id AND EPT.f_wlApp_type = #{appType} AND EPT.f_sys0_del_state = 0
WHERE 1=1 AND EPFG.f_sys0_del_state = 0 AND EPFG.f_start_date <=NOW() AND EPFG.f_expire_date > NOW() AND EPFG.f_left_count > 0 AND EPFG.t_ebuy_fight_supply_merchant_f_id=#{fightMerchantId}
AND EPFG.t_ebuy_product_f_id = #{productId}
ORDER BY EPFG.f_start_date DESC, EPFG.f_id DESC

SELECT ,, ,, , EPFG.f_price_discount fightPrice,date_format(EPFG.f_start_date,'%Y-%m-%d %T') AS startDate,date_format(EPFG.f_expire_date,'%m/%d/%Y %T') AS expireDate,EPFG.f_fight_num fightNum, ifnull(EPP.f_url_mini,(select f_url_mini from t_ebuy_product_fight_pic pfp where pfp.t_ebuy_product_f_id = EPFG.t_ebuy_product_f_id order by f_id desc limit 1)) picUrl,EPFG.f_id fightProductId,ESM.f_name merchantName, CASE WHEN EPFG.f_expire_date < now() THEN 3 WHEN EPFG.f_expire_date > now() AND EPFG.f_start_date > now() THEN 0 WHEN EP.f_left_count <= 0 THEN 2 ELSE 1 END fightStatus, EPS.f_id productShelfId FROM t_ebuy_product_fight_groups EPFG INNER JOIN t_ebuy_product EP ON EPFG.t_ebuy_product_f_id = EP.f_id and EP.f_status = 0 INNER JOIN t_ebuy_product_shelf EPS ON EPS.t_ebuy_product_id = EP.f_id and EPS.f_up_shelf_state = 0
INNER JOIN t_ebuy_product_type ept on EPS.t_ebuy_product_type_id = ept.f_id and ept.f_sys0_del_state = 0 and ept.f_wlApp_type = #{appType}
INNER JOIN t_ebuy_fight_supply_merchant EFSM ON EFSM.f_id = EPFG.t_ebuy_fight_supply_merchant_f_id LEFT JOIN(t_ebuy_fight_order EFO INNER JOIN t_ebuy_order EO ON EO.f_id = EFO.t_ebuy_order_f_id AND EO.f_pay_status=2 ) ON EPFG.f_id = EFO.t_ebuy_product_fight_groups_f_id AND EFO.f_sys0_del_state=0 LEFT JOIN t_ebuy_product_pic EPP ON EPP.t_ebuy_product_f_id=EP.f_id LEFT JOIN t_ebuy_product_introduce_pic EPIP ON EPIP.t_ebuy_product_f_id = EPFG.t_ebuy_product_f_id INNER JOIN t_ebuy_supply_merchant ESM ON ESM.f_id = EP.t_supply_merchant_f_id WHERE 1=1 AND EPFG.f_id=#{productId}

mapper.update:
UPDATE t_ebuy_fight_order EFO
INNER JOIN t_ebuy_product_fight_groups EPFG ON EFO.t_ebuy_product_fight_groups_f_id = EPFG.f_id
	SET EPFG.f_left_count = EPFG.f_left_count-#{fightProductCount} 
	,EPFG.f_sys0_upd_time = SYSDATE()
	WHERE 1=1
AND EPFG.f_sys0_del_state = 0
AND EPFG.f_left_count>=#{fightProductCount} 
AND EFO.t_ebuy_order_f_id=#{orderId}
