mapper.select:
SELECT IF ( EO.f_type = 4, CONCAT( CNPL.f_pay_num, '个月停车费' ), IFNULL(GBBCC.f_bill_name, IF( PB.f_cycle_type=2, CONCAT( IFNULL(PB.f_bill_month_size, ''), '个月', PBT.f_name ), CONCAT( date_format(PB.f_bill_month_start,'%m'), '-', date_format(PB.f_bill_month_end,'%m'), '月', PBT.f_name ) ) )
) AS billName, CONCAT( '￥ ', cast( IF(EO.f_type = 4, CNPL.f_fee, PB.f_amount) / 100 AS DECIMAL (28, 2) ) ) AS billAmount FROM t_ebuy_order_relation EOR INNER JOIN t_ebuy_order EO ON EO.f_id = EOR.f_sub_id AND EO.f_sys0_del_state = 0 LEFT JOIN t_car_num_pay_log CNPL ON CNPL.t_ebuy_order_id = EO.f_id AND CNPL.f_sys0_del_state = 0 LEFT JOIN t_ebuy_order_has_t_pay_bill EOPB ON EOPB.t_ebuy_order_f_id = EO.f_id AND EO.f_sys0_del_state = 0 LEFT JOIN t_pay_bill PB ON PB.f_id = EOPB.t_pay_bill_f_id
LEFT JOIN t_pay_bill_type PBT ON PBT.f_id=PB.f_bill_type_id AND PBT.f_sys0_del_state = 0 LEFT JOIN t_group_building_bill_cycle_config GBBCC ON GBBCC.f_id=PB.t_group_building_bill_cycle_config_f_id WHERE EOR.f_parent_id = #{orderId} AND EOR.f_sys0_del_state = 0

SELECT _tmp.billTitle, _tmp.realAmt amount, _tmp.discountAmt discount, _tmp.name `name`, _tmp.desc `desc`, _tmp.icon, _tmp.typeName typeName, _tmp.orderId, _tmp.type, _tmp.rechargeBill FROM ( ( SELECT OBJ.f_id orderId, IFNULL(OBJ.f_succ_pay_amount, 0) - IFNULL(CSB.f_amount_bt, 0) realAmt, OBJ.f_amount - IFNULL(OBJ.f_succ_pay_amount, 0) + IFNULL(CSB.f_amount_bt, 0) discountAmt, DATE_FORMAT(OBJ.f_pay_time, '%Y年%m月') billTitle, IFNULL(GBBCC.f_bill_name, IF ( OBJ.f_cycle_type = 2, CONCAT( IFNULL(OBJ.f_bill_month_size, ''), '个月', PBT.f_name ), CONCAT( date_format( OBJ.f_bill_month_start, '%m' ), '-', date_format(OBJ.f_bill_month_end, '%m'), '月', PBT.f_name ) ) ) `name`, CONCAT( DATE_FORMAT( IFNULL( OBJ.f_bill_month_start, IFNULL( OBJ.f_bill_month, OBJ.f_month ) ), '%y.%m' ), '-', DATE_FORMAT( IFNULL( OBJ.f_bill_month_end, IFNULL( OBJ.f_bill_month, OBJ.f_month ) ), '%y.%m' ) ) `desc`, PBT.f_icon icon, PBT.f_name typeName, OBJ.f_pay_time payTime, 1 `type`, (OBJ.t_group_building_bill_cycle_config_f_id IS NOT NULL) rechargeBill FROM t_pay_bill OBJ INNER JOIN t_pay_bill_type PBT ON PBT.f_id = OBJ.f_bill_type_id AND PBT.f_sys0_del_state = 0 LEFT JOIN t_ebuy_order_has_t_pay_bill EOPB ON EOPB.t_pay_bill_f_id = OBJ.f_id AND EOPB.f_sys0_del_state = 0 LEFT JOIN t_ebuy_order EO ON EO.f_id = EOPB.t_ebuy_order_f_id AND EO.f_pay_status = 2 AND EO.f_sys0_del_state = 0 LEFT JOIN t_cash_sqpay_bt CSB ON CSB.t_ebuy_order_f_id = EO.f_id INNER JOIN (SELECT 1 type) _type LEFT JOIN t_group_building_bill_cycle_config GBBCC ON GBBCC.f_id=OBJ.t_group_building_bill_cycle_config_f_id WHERE OBJ.t_real_room_f_id = #{realRoomId} AND OBJ.f_sys0_del_state = 0 AND ( OBJ.f_is_pay = 1 OR IFNULL(OBJ.f_finance_status, 0) = 1 )
AND (IF(EO.f_id IS NULL, EOPB.f_id IS NULL, TRUE))
) UNION ( SELECT OBJ.f_id orderId, ( OBJ.f_amount - IFNULL(CSB.f_amount_bt, 0) ) realAmt, ( IFNULL( OBJ.f_total_coupon_amount, 0 ) + IFNULL(CSB.f_amount_bt, 0) ) discountAmt, DATE_FORMAT(OBJ.f_pay_time, '%Y年%m月') billTitle, CONCAT( CNPL.f_pay_num, '个月停车费' ) AS `name`, CNL.t_car_num AS `desc`, '' icon, '停车费' typeName, OBJ.f_pay_time `payTime`, 2 `type`, false rechargeBill FROM t_ebuy_order OBJ INNER JOIN t_ebuy_order_relation EOR ON EOR.f_sub_id = OBJ.f_id AND EOR.f_sys0_del_state = 0 INNER JOIN t_car_num_pay_log CNPL ON CNPL.t_ebuy_order_id = OBJ.f_id AND CNPL.f_sys0_del_state = 0 INNER JOIN t_car_num_list CNL ON CNL.f_id = CNPL.t_car_num_id LEFT JOIN t_cash_sqpay_bt CSB ON CSB.t_ebuy_order_f_id = OBJ.f_id INNER JOIN (SELECT 2 type) _type WHERE OBJ.f_buyer_id = #{userId} AND OBJ.f_type = 4 AND OBJ.f_pay_status = 2 AND OBJ.f_sys0_del_state = 0
) ) _tmp ORDER BY _tmp.payTime DESC

mapper.update:
UPDATE t_paybill_user_prefer
SET f_prefer =#{prefer}
WHERE
	f_paybill_id =#{payBillId}
AND f_user_id =#{userId}
