mapper.sql:
SELECT 


ORDER BY f_id DESC
 LIMIT #{_begin},#{_length}

		UPDATE t_pay_bill

 		 ,f_short_code=#{shortCode}
 		 ,f_amount=#{amount}
 		 ,f_month=str_to_date(#{month},'%Y-%m-%d %T')
 		 ,f_bill_month=str_to_date(#{billMonth},'%Y-%m-%d %T')
 		 ,f_is_pay=#{isPay}
 		 ,f_pay_time=str_to_date(#{payTime},'%Y-%m-%d %T')
 		 ,f_finance_status=#{financeStatus}
 		 ,f_dedu_price=#{deduPrice}
 		 ,f_payment_way=#{paymentWay}
 		 ,t_real_room_f_id=#{tRealRoomFId}
 		 ,f_property_proprietor_id=#{propertyProprietorId}
 		 ,f_succ_pay_amount=#{succPayAmount}
 		 ,f_discount=#{discount}
 		 ,f_prize_record_id=#{prizeRecordId}
 		 ,f_sys0_add_time=str_to_date(#{sys0AddTime},'%Y-%m-%d %T')
 		 ,f_sys0_upd_time=str_to_date(#{sys0UpdTime},'%Y-%m-%d %T')
 		 ,f_sys0_del_time=str_to_date(#{sys0DelTime},'%Y-%m-%d %T')
 		 ,f_sys0_del_time= sysdate() 
 		 ,f_sys0_add_user=#{sys0AddUser}
 		 ,f_sys0_upd_user=#{sys0UpdUser}
 		 ,f_sys0_del_user=#{sys0DelUser}
 		 ,f_bill_time_cfg_id=#{billTimeCfgId}
 		 ,f_bill_type_id=#{billTypeId}
 		 ,f_bill_type_name=#{billTypeName}
 		 ,f_is_prop_fee=#{isPropFee}
 		 ,f_paytime_type=#{paytimeType}
 		 ,f_cycle_type=#{cycleType}
 		 ,f_bill_month_size=#{billMonthSize}
 		 ,f_bill_month_start=str_to_date(#{billMonthStart},'%Y-%m-%d %T')
 		 ,f_bill_month_end=str_to_date(#{billMonthEnd},'%Y-%m-%d %T')
 		 ,f_pay_day_start=str_to_date(#{payDayStart},'%Y-%m-%d %T')
 		 ,f_pay_day_end=str_to_date(#{payDayEnd},'%Y-%m-%d %T')
 		 ,f_hb_amount=#{hbAmount}
 		 ,f_prefer_type=#{preferType}
 		 ,t_bill_cycle_id=#{tBillCycleId}
 		 ,t_group_building_bill_cycle_config_f_id=#{tGroupBuildingBillCycleConfigFId}
 		 ,f_cash_status=#{cashStatus}
 		 ,t_realroom_software_fee_f_id=#{tRealroomSoftwareFeeFId}
 		 ,f_last_unpaid=#{lastUnpaid}
 		 ,f_bank_collection_status=#{bankCollectionStatus}
 		,f_sys0_del_state= 1 

		WHERE 1=1  
		AND f_sys0_del_state = 0 
		AND f_id=#{id}

