mapper.sql:
SELECT 


ORDER BY f_id DESC
 LIMIT #{_begin},#{_length}

		UPDATE t_revenue_apply

 		 ,f_apply_no=#{applyNo}
 		 ,f_rev_apply_finance_id=#{revApplyFinanceId}
 		 ,f_visible_type=#{visibleType}
 		 ,f_mini_role_type=#{miniRoleType}
 		 ,f_mini_role_id=#{miniRoleId}
 		 ,f_goal_type=#{goalType}
 		 ,f_channel=#{channel}
 		 ,f_apply_user_id=#{applyUserId}
 		 ,f_apply_time=str_to_date(#{applyTime},'%Y-%m-%d %T')
 		 ,f_goal_start_time=str_to_date(#{goalStartTime},'%Y-%m-%d %T')
 		 ,f_goal_end_time=str_to_date(#{goalEndTime},'%Y-%m-%d %T')
 		 ,f_total_amount=#{totalAmount}
 		 ,f_tk_status=#{tkStatus}
 		 ,f_opt_type=#{optType}
 		 ,f_tk_succ_time=str_to_date(#{tkSuccTime},'%Y-%m-%d %T')
 		 ,f_sys0_add_time=str_to_date(#{sys0AddTime},'%Y-%m-%d %T')
 		 ,f_sys0_upd_time=str_to_date(#{sys0UpdTime},'%Y-%m-%d %T')
 		 ,f_sys0_del_time=str_to_date(#{sys0DelTime},'%Y-%m-%d %T')
 		 ,f_sys0_del_time= sysdate() 
 		 ,f_sys0_add_user=#{sys0AddUser}
 		 ,f_sys0_upd_user=#{sys0UpdUser}
 		 ,f_sys0_del_user=#{sys0DelUser}
 		 ,f_role_name=#{roleName}
 		 ,f_b_phone=#{bPhone}
 		 ,f_b_connect_name=#{bConnectName}
 		 ,f_b_bank_no=#{bBankNo}
 		 ,f_b_bank_name=#{bBankName}
 		 ,f_account_name=#{accountName}
 		 ,f_bank_branch=#{bankBranch}
 		 ,f_bank_province=#{bankProvince}
 		 ,f_bank_city=#{bankCity}
 		 ,f_amount_ptbt=#{amountPtbt}
 		 ,f_amount_usr_real=#{amountUsrReal}
 		 ,f_eas_bill_numbers=#{easBillNumbers}
 		 ,f_audit_time=str_to_date(#{auditTime},'%Y-%m-%d %T')
 		 ,f_audit_status=#{auditStatus}
 		 ,f_apply_note=#{applyNote}
 		 ,f_handler_note=#{handlerNote}
 		 ,t_ebuy_supply_merchant_fk=#{tEbuySupplyMerchantFk}
 		 ,f_revenue_type=#{revenueType}
 		 ,f_revenue_rate=#{revenueRate}
 		 ,f_eas_payed_amount=#{easPayedAmount}
 		 ,t_gb_id=#{tGbId}
 		 ,t_property_management_f_id=#{tPropertyManagementFId}
 		,f_sys0_del_state= 1 

		WHERE 1=1  
		AND f_sys0_del_state = 0 
		AND f_id=#{id}

